    formatDate(date) {
      if (!date) return 'N/A';
      try {
        if (typeof date === 'string') {
          date = new Date(date);
        }
        if (!(date instanceof Date) || isNaN(date.getTime())) {
          return 'N/A';
        }
        return date.toLocaleDateString('en-US', {
          month: 'short',
          day: 'numeric',
